import React, {Component} from 'react';

class MenuOption extends Component {
    constructor(props) {
        super(props) 
            this.state = {
                counter:0
            }

            this.getItemLocalStorage = () => (
                JSON.parse(localStorage.getItem('request'))
            )

            this.decrement = () => {
                const request = this.getItemLocalStorage()
                const actualProduct = request.products.filter(product => product.name === this.props.name)
                console.log(actualProduct)
                // if(actualProduct.lenght > 0){
                //     request.products !== actualProduct
                // }
                // request.products.push()
                // this.setState({counter : this.state.counter - 1})
                // localStorage.setItem('request', JSON.stringify(request))
            }

            this.increment = () => {
                this.setState({counter : this.state.counter + 1})
            }
        }



    render() {
        return (
            <section>
                <div>
                <img src={this.props.url_image} style={{maxWidth:'150px'}}/>
                <p>{this.props.name}</p>
                <p> R$ {this.props.price},00</p>
                </div>
            <div>
                <button onClick={this.decrement}>-</button>
                <h1>{this.state.counter}</h1>
                <button onClick={this.increment}>+</button>
            </div>

            </section>
        )
    }
}




export default MenuOption
