import React, { Component } from "react";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import Typography from "@material-ui/core/Typography";
import { withStyles } from "@material-ui/core/styles";
import fire from 'firebase'
import moment from 'moment'
import CircularProgress from '@material-ui/core/CircularProgress';
// import MenuOption from '../components/menu-option'

class MenuTabs extends Component {
  constructor(props) {
    super(props)
      this.state = {
        menu: {},
        activeIndex: 0,
        isLoading: true,
      };
    this.menuOptions = this.menuOptions.bind(this);  
  }

  handleChange = (_, activeIndex) => this.setState({ activeIndex });

  getMenu () {
    return fire.firestore().collection('menu')
    .get()
    .then((snap) => {
      snap.forEach(doc => {
        if(
          moment() >= moment(doc.data().initiate_at, 'HH:mm') 
          && moment() <= moment(doc.data().end_at, 'HH:mm') 
        ) {
            this.setState({ menu: doc.data() })
          }
      }) 
    }) 
    .catch(err => console.log(err))
  }

  async componentDidMount(){
    await this.getMenu()
    this.menuOptions()
    this.setState({ isLoading: false })
  }

  filterLabels() {
    const menuLabel = Object.keys(this.state.menu);
    const filteredLabel = menuLabel.filter(label => label !== 'initiate_at' && label !== 'end_at')
    filteredLabel.push('Resumo')
    return filteredLabel
  }

  handleLabel() {
    return this.filterLabels().map((labelToRender, index ) => {
      return <MyTab label = {labelToRender} key={index} onClick={this.menuOptions} />
    })
  }

  menuOptions() {
    const filteredLabel = this.filterLabels()
    setTimeout(() => {
      const selectedMenu = this.state.menu[filteredLabel[this.state.activeIndex]]
      console.log(selectedMenu)
    }, 500)
    // selectedMenu.map((actualIndex) => {
    //   // <MenuOption actualIndex />
    // })
  }

  render() {
    const { activeIndex, isLoading } = this.state;
      return (
        <>
          {isLoading ? <Load /> :    
            <div
              style={{
                display: "flex"
              }}
            >

            <p>Menu</p>

            <VerticalTabs value={activeIndex} onChange={this.handleChange}  >
              {this.handleLabel()}  
            </VerticalTabs>

            {activeIndex === 0 && <TabContainer></TabContainer>}

            <p>{this.state.activeIndex}</p>
            </div>
          }   
        </>
      );
  }

}

const VerticalTabs = withStyles(theme => ({
  flexContainer: {
    flexDirection: "column"
  },

  indicator: {
    display: "none"
  }
}))(Tabs);

const MyTab = withStyles(theme => ({
  root: {
    backgroundColor: "#e1e1e1",
    borderRight: '1px solid #737373',
    color: "white",
  },
  wrapper: {
    backgroundColor: "#737373",
    padding: theme.spacing(2),
  },
  selected: {
    borderRight: "4px solid #34b6a6",
  }
}))(Tab);


const Load = withStyles(theme => ({
  root:{
    color: '#34b6a6'
  }
}))(CircularProgress)

function TabContainer(props) {
  return (
    <Typography component="div" style={{ padding: 24 }}>
      {props.children}
    </Typography>
  );
}

export default MenuTabs;
