import React, { Component } from 'react'
import fire from '../../config/firebase';
import { withRouter } from 'react-router-dom';
import AddIcon from '@material-ui/icons/Add';
import Fab from '@material-ui/core/Fab';


class Waiter extends Component {
    constructor(props) {
        super(props);
        this.state={
            requestList:[]
        }

        this.logout = this.logout.bind(this)
        this.clientInfo = this.clientInfo.bind(this)
        this.getDelivery = this.getDelivery.bind(this)
        this.delivery = this.delivery.bind(this)

        // this.menu = this.menu.bind(this)
    }

    logout() {
        fire.auth().signOut()
        .then(() => this.props.history.push('/login'))
        .catch( error => error );
    }

    getDelivery() {
        return fire.firestore().collection('requests')
        .get()
        .then((request) => {
            const requestList = []       
                    request.forEach(doc => requestList.push({...doc.data(), id: doc.id}) )
                    this.setState({requestList: this.state.requestList.status === 'done' })
                    console.log(this.state.requestList)
        })
        .catch(err => err)
    }

    // delivery() {
    //     const ready = this.getDelivery()
    //     if (this.state.requestList.status === 'done'){
    //         console.log(ready)
    //     }
    // }



    // menu() {
    //      this.props.history.push('/menu')
    // }

    clientInfo () {
        this.props.history.push('/client')

   }

    render() {
        return(
            <div>
                <h1>Waiter</h1>
                <div>
                    <p>Prontos para a entrega :</p>
                    {this.getDelivery()}
                </div>
                <div></div>
                <p>Fazer um novo pedido</p>
                <Fab onClick={this.clientInfo} color="primary" aria-label="add">
                <AddIcon />
                </Fab>
                <button onClick={this.logout}>sair</button>

            </div>
        )
    }
}

export default withRouter(Waiter)
