import React, { Component } from 'react'
import fire from '../../config/firebase';
import { withRouter } from 'react-router-dom';
import AddIcon from '@material-ui/icons/Add';
import Fab from '@material-ui/core/Fab';
import Button from '@material-ui/core/Button';


class Waiter extends Component {
    constructor(props) {
        super(props);
        this.state={
            requestList:[]
        }
        this.clientInfo = this.clientInfo.bind(this)
        this.getDelivery = this.getDelivery.bind(this)
        // this.menu = this.menu.bind(this)
    }



    getDelivery() {
        return fire.firestore().collection('requests')
        .get()
        .then((request) => {
            const requestList = []       
                    request.forEach(doc => requestList.push({...doc.data(), id: doc.id}) )
                    this.setState({requestList: requestList.filter(request => request.status === 'done')})
        })
        .catch(err => err)
    }

    loadClientDelivery(){
       return this.state.requestList.map((data, index) => (
           <div key={index}>
               {data.name}
               {data.table}
            <Button onClick= {() => this.updateRequest(data.id, 'delivered' )}> Pedido Entregue </Button>
           </div> 
        ))
    }


        async componentDidMount() {
            await this.getDelivery()
        }

       async updateRequest (id , status)  {          
            await fire.firestore().collection('requests').doc(id).update({
                status,
            })
                this.getDelivery()
        }



    // menu() {
    //      this.props.history.push('/menu')
    // }

    clientInfo () {
        this.props.history.push('/client')

   }

    render() {
        return(
            <div>
                <h1>Waiter</h1>

                <div>
                    <p>Prontos para a entrega :</p> 
                    {this.loadClientDelivery()}
                </div>
                <div></div>
                <p>Fazer um novo pedido</p>
                <Fab onClick={this.clientInfo} color="primary" aria-label="add">
                <AddIcon />
                </Fab>


            </div>
        )
    }
}

export default Waiter
