import React, { Component } from 'react'
import { BrowserRouter, Route, Switch, L } from 'react-router-dom'
import { CssBaseline } from '@material-ui/core'
import Login from './pages/login/login'
import fire from './config/firebase';

import Waiter from './pages/waiter/waiter'
// import Cooker from './pages/cooker/cooker'


class App extends Component {
    constructor(props) {
        super(props)
        this.state = {
            user: null,
            componentToLoad: null
        }
    }

    componentDidMount(){
      this.authListner();
    }

    routerConfig() {
    return(
      <div>
      <CssBaseline />
      <BrowserRouter>
      <Switch>
          <Route path='/login'  component={Login} />
          <Route path='/' component={Waiter} />
          {/* <Route path='/' component={this.state.componentToLoad} /> */}
      </Switch>
      </BrowserRouter>
    </div>
    )
  }



  authListner() {
    fire.auth().onAuthStateChanged((user) => {
        if (user) {
            this.setState({user});
        } else {
            this.setState({user: null})
        }
    })
  }

  render() {
    return (
    <div>
      {this.routerConfig()}
    </div>
    )
    }

}

export default App
