  //                 this.setState({ menu: doc.data() })
  //               }
  //             })


  //     }) 
  //     .catch(err => console.log(err))
  // }

  // componentDidMount(){
  //     this.getMenu()
  //     console.log(this.style())
  // }

  a11yProps(index) {
    return {
    id: `vertical-tab-${index}`,
    'aria-controls': `vertical-tabpanel-${index}`,
    };
  }


  // estiliza
  style(){
    return {
      root: {
        flexGrow: 1,  
        backgroundColor: 'red',
        display: 'flex',
        height: 224,
      },
      tabs: {
        borderRight: '1px solid red' ,
      },
    }
  }

  verticalTabs() {
      const classes = this.style();   
      return (
        <div className={classes.root}>
          <Tabs
            orientation="vertical"
            value={this.state.value}
            onChange={this.handleChange}
            className={classes.tabs}
          >
            <Tab label="lanches" {...this.a11yProps(0)} />
            <Tab label="acompanhamentos" {...this.a11yProps(1)} />
            <Tab label="Bebidas" {...this.a11yProps(2)} />
            <Tab label="Resumo" {...this.a11yProps(3)} />
          </Tabs>

          <TabPanel value={this.state.value} index={0}>
            opçoes dos lanches
          </TabPanel>
          <TabPanel value={this.state.value} index={1}>
            opções de acompanhamentos
          </TabPanel>
        </div>
      );
    }

