import React, { Component } from 'react';
import fire from 'firebase';
import Button from '@material-ui/core/Button';



class CookerComponent extends Component{
    constructor(props){
        super(props)
        this.state={
            requestList: [],
            toDo: true,

        }
    this.getRequestFirebase = () => {
        return fire.firestore().collection('requests')
        .get()
        .then((request) => {
            const requestList = []
            request.forEach(doc => requestList.push(doc.data()))
            this.setState({requestList})
        })
        .catch(err => err)
        }

        this.createRestaurantManager = () => {
             return this.state.requestList.map((actualRequest,) => (
                 actualRequest.products.map((arrRequest, index) => (
                     <div key={index}>
                     <p>Mesa {actualRequest.table}</p>
                    <p> {arrRequest.quantity} </p>
                     <p> {arrRequest.name} </p>
                     <p> {arrRequest.options || ''} </p>
                     <p> {arrRequest.extras.name || ''} </p>
                     <Button onClick={this.prepare} disabled={actualRequest.disabled}>Preparar</Button>
                     </div>
                 ))
             ))
        }

        this.prepare = (actualRequest) => {
            actualRequest.disabled = true;
            console.log(actualRequest)
            this.setState({requestList: this.state.requestList});



        console.log(this.state.requestList)

        }
    }

    componentWillMount(){
        this.getRequestFirebase()
    }



    render(){
        return(
            <div>
                {this.createRestaurantManager()}

            </div>
        )
    }
}


export default CookerComponent
