import React, {Component} from 'react';

class MenuOption extends Component {
  constructor(props) {
    super(props) 
      this.state = {
          counter:0
      }

      this.getItemLocalStorage = () => (
        JSON.parse(localStorage.getItem('request'))
      )

      this.getActualProduct = () => {
        const request = this.getItemLocalStorage()
        return request.products.filter(product => product.name === this.props.name)
      }

      this.decrement = () => {
        const request = this.getItemLocalStorage()
        request.products = request.products.map(product => {
          if (product.name === this.props.name) {
              product.quantity -= 1
          }
          return product
          })
        this.setState({counter : this.state.counter - 1})
        localStorage.setItem('request', JSON.stringify(this.removeEmptyProduct(request)))
      }

      this.increment = () => {
        const actualProduct = this.getActualProduct()
        const request = this.getItemLocalStorage()
          if(actualProduct.length === 0 ) {
            request.products.push({
              name: this.props.name,
              price: this.props.price,
              quantity: 1
            })   
          } else {
            request.products = request.products.map(product => {
              if (product.name === this.props.name) {
                product.quantity += 1
              }
            return product
            })
          } 

        this.setState({counter : this.state.counter + 1})
        localStorage.setItem('request', JSON.stringify(this.removeEmptyProduct(request)))
      }

      this.removeEmptyProduct = (request) => {
        request.products = request.products.filter(product => product.quantity !== 0)
        return request
      }
  }

  componentDidMount() {
    const actualProduct = this.getActualProduct()[0]
      if (actualProduct) {
        this.setState({counter: actualProduct.quantity})
      }
  }    

  render() {
    return (
      <section>
        <div>
          <img src={this.props.url_image} style={{maxWidth:'150px'}}/>
          <p>{this.props.name}</p>
          <p> R$ {this.props.price},00</p>
        </div>
        <div>
          <button disabled={this.state.counter === 0} onClick={this.decrement}>-</button>
          <h1>{this.state.counter}</h1>
          <button onClick={this.increment}>+</button>
        </div>
      </section>
    )
  }
}




export default MenuOption
