    // }

    menu (){
        return fire.firestore().collection('menu')
        // .where()
        // .where()
        .get()
        .then((snap) => {
            snap.forEach(docs => {
                this.setState({ name: docs.data() })
                console.log(docs.data())
              })
        }) 
        .catch(err => console.log(err))
    }


render() {
    return(
        <h1> menu </h1>
    )
}
}

export default MenuComponent
