import React, { Component } from 'react'
import fire from '../config/firebase';
import moment from 'moment';
import { Styles } from '@material-ui/core/styles';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';

function TabPanel(props) {
  const { children, value, index} = props;
    return (
      <Typography>
        {value === index && <Box p={3}>{children}</Box>}
      </Typography>
  );
}

class MenuComponent extends Component {
  constructor(props){
      super(props) 
      this.state = {
          menu: {},
          value: 0
      }
  }

  handleChange = (_, value) => this.setState({ value })

  getMenu () {
      return fire.firestore().collection('menu')
      .get()
      .then((snap) => {
          snap.forEach(doc => {
              if(moment() >= moment(doc.data().initiate_at, 'HH:mm') && moment() <= moment(doc.data().end_at, 'HH:mm') ){
                  this.setState({ menu: doc.data() })
                }
              })


      }) 
      .catch(err => console.log(err))
  }

  componentDidMount(){
      this.getMenu()
      console.log(this.style())
  }

  a11yProps(index) {
    return {
    id: `vertical-tab-${index}`,
    'aria-controls': `vertical-tabpanel-${index}`,
    };
  }


  // estiliza
  style(){
    return Styles(theme => ({
      root: {
        flexGrow: 1,
        backgroundColor: theme.palette.background.paper,
        display: 'flex',
        height: 224,
      },
      tabs: {
        borderRight: `1px solid ${theme.palette.divider}`,
      },
      }))
  }

  verticalTabs() {
      const classes = this.style();   
      return (
        <div className={classes.root}>
          <Tabs
            orientation="vertical"
            value={this.state.value}
            onChange={this.handleChange}
            className={classes.tabs}
          >
            <Tab label="lanches" {...this.a11yProps(0)} />
            <Tab label="acompanhamentos" {...this.a11yProps(1)} />
            <Tab label="Bebidas" {...this.a11yProps(2)} />
            <Tab label="Resumo" {...this.a11yProps(3)} />
          </Tabs>

          <TabPanel value={this.state.value} index={0}>
            opçoes dos lanches
          </TabPanel>
          <TabPanel value={this.state.value} index={1}>
            opções de acompanhamentos
          </TabPanel>
        </div>
      );
    }

    render() {

        return(
           <div>
             {this.verticalTabs()}
           </div>
        )
    }
}

export default MenuComponent
