import React, { Component } from 'react'
import fire from '../config/firebase';
import moment from 'moment';
import { withStyles } from '@material-ui/core/styles';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';

function TabPanel(props) {
  const { children, value, index} = props;
    return (
      <Typography>
        {value === index && <Box p={3}>{children}</Box>}
      </Typography>
  );
}

class MenuComponent extends Component {
  constructor(props){
      super(props) 
      this.state = {
          menu: {},
          value: 0,
      }
  }

  handleChange = (_, value) => this.setState({ value })

  a11yProps(index) {
    return {
    id: `vertical-tab-${index}`,
    'aria-controls': `vertical-tabpanel-${index}`,
    };
  }

  verticalTabs() {
      const classes = this.style();   
      return (
        <div style={{
          display: 'flex'
        }}>
          <Tabs
            orientation="vertical"
            value={this.state.value}
            onChange={this.handleChange}
            className={classes.tabs}
          >
            <Tab label="lanches" {...this.a11yProps(0)} />
            <Tab label="acompanhamentos" {...this.a11yProps(1)} />
            <Tab label="Bebidas" {...this.a11yProps(2)} />
            <Tab label="Resumo" {...this.a11yProps(3)} />
          </Tabs>

          <TabPanel value={this.state.value} index={0}>
            opçoes dos lanches
          </TabPanel>
          <TabPanel value={this.state.value} index={1}>
            opções de acompanhamentos
          </TabPanel>
        </div>
      );
    }

    render() {

        return(
           <div>
             {this.verticalTabs()}
           </div>
        )
    }
}

  // estiliza

  const verticalTabs = withStyles(theme => ({
    flexContainer: {
      flexDirection: "column"
    },
    indicator: {
      display: "none"
    }
  }))(Tabs);

  const myTab = withStyles(theme => ({
    root: {
      backgroundColor: "#ccc",
      borderRadius: theme.shape.borderRadius
    },
    wrapper: {
      backgroundColor: "#ddd",
      padding: theme.spacing(2),
      borderRadius: theme.shape.borderRadius
    },
    selected: {
      color: "tomato",
      borderBottom: "2px solid tomato"
    }
  }))(Tab);



export default MenuComponent
