import React, { Component } from 'react'
import fire from '../config/firebase';
import moment from 'moment';
import { makeStyles } from '@material-ui/core/styles';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';




class MenuComponent extends Component {
  constructor(props){
      super(props) 
      this.state = {
          menu: {}

      }
  }

  getMenu () {
      return fire.firestore().collection('menu')
      .get()
      .then((snap) => {
          snap.forEach(doc => {
              if(moment() >= moment(doc.data().initiate_at, 'HH:mm') && moment() <= moment(doc.data().end_at, 'HH:mm') ){
                  this.setState({ menu: doc.data() })
                }
              })


      }) 
      .catch(err => console.log(err))
  }

  componentDidMount(){
      this.getMenu()
  }

  // precisa dessa função para aparecer as opçoes ao lado 
  tabPanel(props) {
  const { children, value, index} = props;
  return (
    <Typography>
      {value === index && <Box p={3}>{children}</Box>}
    </Typography>
  );
}

    // pega o index da tab atual e seta o id e deixa o id dinâmico
  a11yProps(index) {
    return {
    id: `vertical-tab-${index}`,
    'aria-controls': `vertical-tabpanel-${index}`,
    };
  }


  // estiliza
  style(){
  const useStyles = makeStyles(theme => ({
    root: {
      flexGrow: 1,
      backgroundColor: theme.palette.background.paper,
      display: 'flex',
      height: 224,
    },
    tabs: {
      borderRight: `1px solid ${theme.palette.divider}`,
    },
  }));
  }

  verticalTabs() {
      const classes = style();
      const [value, setValue] = React.useState(0);
      const handleChange = (event, newValue) => {
        setValue(newValue);
      };

      return (
        <div className={classes.root}>
          <Tabs
            orientation="vertical"
            value={value}
            onChange={handleChange}
            className={classes.tabs}
          >

          {/* cria as opções do lado esquerdo */}
            <Tab label="lanches" {...a11yProps(0)} />
            <Tab label="acompanhamentos" {...a11yProps(1)} />
            <Tab label="Bebidas" {...a11yProps(2)} />
            <Tab label="Resumo" {...a11yProps(3)} />
          </Tabs>

          {/* cria as opções do lado direito */}
          <TabPanel value={value} index={0}>
            opçoes dos lanches
          </TabPanel>
          <TabPanel value={value} index={1}>
            opções de acompanhamentos
          </TabPanel>
        </div>
      );
    }

    render() {
        return(
           <div>
             <h1>menu</h1>
           </div>
        )
    }
}

export default MenuComponent












