import React, { Component } from 'react'
import { Button, TextField } from '@material-ui/core';


class Client extends Component {
    constructor(props) {
        super(props)
        this.state = {
            name: '',
            table: ''
        }
        this.handleChange = this.handleChange.bind(this);
        this.newRequest = this.newRequest.bind(this);
        // this.handleChange = this.handleChange.bind(this);

    }

    newRequest(e) {
         e.preventDefault();
         localStorage.setItem('request', JSON.stringify({
             name: this.state.name,
             table: this.state.table
         })) 

    }

    loadClientData() {
        const requestObject = JSON.parse(localStorage.getItem('request'))
        if(requestObject){
            this.setState({ name: requestObject.name })
            this.setState({ table: requestObject.table })
        }
    }

    componentDidMount() {
        this.loadClientData()
    }

    handleChange(e) {
        this.setState({ [e.target.name]: e.target.value });
      }

    render() {
        return(
        <div>
        <form onSubmit={this.newRequest}>
        <p> Informações do cliente: </p>
        <TextField type='text' placeholder='Nome' name='name' value={this.state.name} onChange={this.handleChange} />
        <TextField type='number' placeholder='Mesa' name='table' value={this.state.table} onChange={this.handleChange} />
        <Button type='submit'> Próximo > </Button>
        </form>
        </div>
        )
    }

}


export default Client
