import React, {Component} from 'react';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import { makeStyles } from '@material-ui/core/styles';


class MenuOption extends Component {
  constructor(props) {
    super(props) 
      this.state = {
          counter:0,
          modal: false
      }

      this.getItemLocalStorage = () => (
        JSON.parse(localStorage.getItem('request'))
      )

      this.getActualProduct = () => {
        const request = this.getItemLocalStorage()
        return request.products.filter(product => product.name === this.props.name)
      }

      this.decrement = () => {
        const request = this.getItemLocalStorage()
        request.products = request.products.map(product => {
          if (product.name === this.props.name) {
              product.quantity -= 1
          }
          return product
          })
        this.setState({counter : this.state.counter - 1})
        localStorage.setItem('request', JSON.stringify(this.removeEmptyProduct(request)))
      }

      this.increment = () => {
        const actualProduct = this.getActualProduct()
        const request = this.getItemLocalStorage()
        console.log(this.props.options)
          if(actualProduct.length === 0 ) {
            request.products.push({
              name: this.props.name,
              price: this.props.price,
              quantity: 1,
              options: this.props.options,
              extras: this.props.extras
            })   
          } else {
            request.products = request.products.map(product => {
              if (product.name === this.props.name) {
                product.quantity += 1
              }
            return product
            })
          } 

        this.setState({counter : this.state.counter + 1})
        localStorage.setItem('request', JSON.stringify(this.removeEmptyProduct(request)))
      }

      this.removeEmptyProduct = (request) => {
        request.products = request.products.filter(product => product.quantity !== 0)
        return request
      }

      this.selectOptionsAndExtras = () => {
        this.setState({modal: true})
      }

        const useStyles = makeStyles(theme => ({
    modal: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    },
    paper: {
      backgroundColor: theme.palette.background.paper,
      border: '2px solid #000',
      boxShadow: theme.shadows[5],
      padding: theme.spacing(2, 4, 3),
    },
  }));
  }



  componentDidMount() {
    const actualProduct = this.getActualProduct()[0]
      if (actualProduct) {
        this.setState({counter: actualProduct.quantity})
      }
  }  



  render() {
    return (
      <section>
        <div>
          <img src={this.props.url_image} style={{maxWidth:'150px'}}/>
          <p>{this.props.name}</p>
          <p> R$ {this.props.price},00</p>
        </div>
        <div>
          <button disabled={this.state.counter === 0 || this.props.options || this.props.extras} onClick={this.decrement}>-</button>
          <h1>{this.state.counter}</h1>
          <button onClick={this.props.options && this.props.extras ? this.selectOptionsAndExtras : this.increment}>+</button>
        </div>
        <Modal
          aria-labelledby="transition-modal-title"
          aria-describedby="transition-modal-description"
          open={this.state.modal}
          onClose={() => this.setState({modal: false})}
          closeAfterTransition
          BackdropComponent={Backdrop}
          BackdropProps={{
            timeout: 500,
          }}
          style = {{ display: 'flex',
          alignItems: 'center',
          justifyContent: 'center'}}
        >
          <Fade in={this.state.modal}>
            <div>
              <h2 id="transition-modal-title">Transition modal</h2>
              <p id="transition-modal-description">Oi igor, obrigada por me ensinar as coisas! um dia eu vou ser rika e te comprar uma espada do sasuke </p>
            </div>
        </Fade>
      </Modal>
      </section>
    )
  }
}




export default MenuOption
