import React, { Component } from 'react';
import fire from 'firebase';
import Button from '@material-ui/core/Button';



class CookerComponent extends Component{
    constructor(props){
        super(props)
        this.state={
            requestList: []            
        }

        this.getRequestFirebase = () => {
            return fire.firestore().collection('requests')
            .get()
            .then((request) => {
                const requestList = []
                request.forEach(doc => requestList.push({...doc.data(), id: doc.id}) )
                this.setState({requestList})
            })
            .catch(err => err)
        }

        this.updateRequest = async (id , status) => {          
            await fire.firestore().collection('requests').doc(id).update({
                status,
                cookerId: fire.auth().currentUser.uid
            })
                this.getRequestFirebase()


        }

        this.buttonName = (status) => {
            if(status === 'toDo') {
                return 'Preparar'   
            }
            else if(status === 'doing'){
                return 'Em preparo...'
            }
            else if(status === 'done') {
                return 'Pronto !'
            }
        }

        this.createRestaurantManager = () => {
             return this.state.requestList.map((actualRequest, actualIndex) => (
                 <div key={actualIndex}>
                {actualRequest.products.map((arrRequest, index) => (
                     <div key={index}>
                     <p>Mesa {actualRequest.table}</p>
                     <p> {arrRequest.quantity} </p>
                     <p> {arrRequest.name} </p>
                     <p> {arrRequest.options || ''} </p>
                     <p> {arrRequest.extras ? arrRequest.extras.name : ''} </p>
                     </div>
                ))}
                 <Button 
                 onClick={() => this.updateRequest(actualRequest.id, 'doing' )} 
                 disabled={actualRequest.status === 'doing'}
                 >
                     {this.buttonName(actualRequest.status)}
                 </Button>
                 </div>
             ))
        }
    }

    componentWillMount(){
        this.getRequestFirebase()
    }

    render(){
        return(
            <div>
                {this.createRestaurantManager()}

            </div>
        )
    }
}


export default CookerComponent
