import React, { Component, Fragment } from 'react';
import { Button, TextField } from '@material-ui/core';
import fire from '../../config/firebase';
import Logo from '../../assets/logo/logo.png';
import Waiter from './waiter';



class Login extends Component {
    constructor(props) {
        super(props)
        this.state = {
            email: '',
            password: ''
        }

        this.setState = this.setState.bind(this);
    }

    login(e) {
        e.preventDefault();
        fire.auth().signInWithEmailAndPassword(this.state.email, this.state.password).then((u))
    }

    handleChange(e) {
        this.setState({ [e.target.name]: e.target.value })
    }

    render() {
        return (
            <div className=''>
                <Fragment>
                <img style={{maxWidth: '200px'}} src={Logo} alt='Logo' />
                </Fragment>
                <form noValidate>
                    <TextField  value={this.state.email} onChange={this.handleChange} type="email" placeholder="email" variant="filled"/>
                    <TextField  value={this.state.password} onChange={this.handleChange} type="password" placeholder="senha" variant="filled"/>
                    <Button> Entrar </Button>
                </form>
            </div>
        )
    }
}


class Auth extends Component {
    constructor (props){
        super(props);
        this.state = {
            user:{},
        }

    }

    componentDidMount(){
        this.authListner();
    }

    authListner() {
        fire.auth().onAuthStateChanged((user) => {
            if (user) {
                this.setState({user});
            } else {
                this.setState({user: null})
            }
        })
    }

    render() {
        return(
            <div className='Auth'>
            {this.state.user ? (<Waiter />) : (<Login/>)}
            </div>
        );
    }
}




export default Login
