import React, { Component } from "react";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import Typography from "@material-ui/core/Typography";
import { withStyles } from "@material-ui/core/styles";
import fire from 'firebase'
import moment from 'moment'

class ProfileTabs extends Component {
  constructor(props) {
    super(props)
    this.state = {
      menu: {},
      activeIndex: 0,
    };
  }

  handleChange = (_, activeIndex) => this.setState({ activeIndex });

  getMenu () {
    return fire.firestore().collection('menu')
    .get()
    .then((snap) => {
      snap.forEach(doc => {
        if(
          moment() >= moment(doc.data().initiate_at, 'HH:mm') 
          && moment() <= moment(doc.data().end_at, 'HH:mm') 
        ){
            this.setState({ menu: doc.data() })
        }
      }) 
    }) 
    .catch(err => console.log(err))
  }

  async componentDidMount(){
    await this.getMenu()
    console.log(this.state.menu)
  }
  render() {
    const { activeIndex } = this.state;
    return (
      <div
        style={{
          display: "flex"
        }}
      >
        <VerticalTabs value={activeIndex} onChange={this.handleChange}>
          <MyTab label="item one" />
          <MyTab label="item two" />
          <MyTab label="item three" />
        </VerticalTabs>

        {activeIndex === 0 && <TabContainer>Item One</TabContainer>}
        {activeIndex === 1 && <TabContainer>Item Two</TabContainer>}
        {activeIndex === 2 && <TabContainer>Item Three</TabContainer>}
      </div>
    );
  }
}

const VerticalTabs = withStyles(theme => ({
  flexContainer: {
    flexDirection: "column"
  },
  indicator: {
    display: "none"
  }
}))(Tabs);

const MyTab = withStyles(theme => ({
  root: {
    backgroundColor: "#ccc",
    borderRadius: theme.shape.borderRadius,
  },
  wrapper: {
    backgroundColor: "#ddd",
    padding: theme.spacing(2),
    borderRadius: theme.shape.borderRadius,
  },
  selected: {
    color: "tomato",
    borderBottom: "2px solid tomato",
  }
}))(Tab);

function TabContainer(props) {
  return (
    <Typography component="div" style={{ padding: 24 }}>
      {props.children}
    </Typography>
  );
}

export default ProfileTabs;
