import React, { Component } from 'react'
import { Button, TextField } from '@material-ui/core';


class Client extends Component {
    constructor(props) {
        super(props)
        this.state = {
            name: '',
            table: ''
        }

    }

    handleChange(e) {
        this.setState({ [e.target.name]: e.target.value });
      }

    render() {
        return(
        <div>

        <p> Informações do cliente: </p>
        <TextField type='text' placeholder='Nome' name='name' value={this.state.name} onChange={this.state.handleChange} />
        <TextField type='number' placeholder='Mesa' name='table' value={this.state.table} onChange={this.state.handleChange} />
        <Button type='submit'> Próximo > </Button>
        </div>
        )
    }

}


export default Client
