import React, { Component } from 'react';
import Counter from '../counter/counter';
import fire from '../../config/firebase';



class Resume extends Component {
    constructor(props){
        super(props);
        this.state={
            request: []
        }

        this.getItemLocalStorage = () => (
            JSON.parse(localStorage.getItem('request'))
          )


        this.loadProducts = () => {
            return this.state.request.products.map((actualProduct, index) => (
                <li key={index}>
                {actualProduct.quantity}
                {actualProduct.name}
                <div>
                {actualProduct.options ? `'Hambúrguer' ${actualProduct.options}` : ''}
                {actualProduct.extras ? actualProduct.extras.name : ''}
                </div>
                R${actualProduct.extras ? actualProduct.price + actualProduct.extras.price : actualProduct.price * actualProduct.quantity } ,00
                <span onClick={this.updateRequest} ><Counter isResume={true} name={actualProduct.name} price={actualProduct.price} options={actualProduct.options} extras={actualProduct.extras} /></span>
                </li>
                ))   
        }

        this.updateRequest=() => {
            this.setState({request: this.getItemLocalStorage()}) 
        }

        this.sum = () => {
            return this.state.request.products.reduce((LastSumProduct, actualProduct) => {
               if (actualProduct.extras && actualProduct.extras.price) {
                   return ((LastSumProduct + actualProduct.price) + actualProduct.extras.price) * actualProduct.quantity
               } 
               return (LastSumProduct + actualProduct.price) * actualProduct.quantity
            }, 0)
        }

        this.sendRequestToFirebase = () => {
            fire.firestore.collection('requests').doc().set(this.state.request);
        }


    }

    componentWillMount(){
        this.updateRequest() 
    }

    render(){
        return(
            <div>
                <h1>Resumo</h1>
                <ul>
                {this.loadProducts().length > 0 ? this.loadProducts() : 'O pedido está vazio!' }
                <button disabled={!this.loadProducts().length}>Cancelar pedido</button>
                <button disabled={!this.loadProducts().length} onClick={this.sendRequestToFirebase}>Enviar pedido para a cozinha</button>
                </ul>
                <h3>Total: {this.sum()}</h3>
            </div>
        )
    }

}

export default Resume;
