import React, { Component } from 'react';
import Counter from '../counter/counter';


class Resume extends Component {
    constructor(props){
        super(props);
        this.state={
            request: []
        }

        this.getItemLocalStorage = () => (
            JSON.parse(localStorage.getItem('request'))
          )


        this.loadProducts = () => {
            return this.state.request.products.map((actualProduct, index) => (
                <li key={index}>
                {actualProduct.quantity}
                {actualProduct.name}
                <div>
                {actualProduct.options ? `'Hambúrguer' ${actualProduct.options}` : ''}
                {actualProduct.extras ? actualProduct.extras.name : ''}
                </div>
                R${actualProduct.extras ? actualProduct.price + actualProduct.extras.price : actualProduct.price } ,00
                <Counter onClick={this.updateRequest() } name={actualProduct.name} price={actualProduct.price} options={actualProduct.options} extras={actualProduct.extras} />
                </li>
                ))   
        }

        this.updateRequest=() => {
            this.setState({request: this.getItemLocalStorage()}) 
        }

    }

    componentWillMount(){
        this.updateRequest() 
    }

    render(){
        return(
            <div>
                <h1>oi</h1>
                <ul>
                {this.loadProducts()}
                </ul>
            </div>
        )
    }

}

export default Resume;
