import React, { Component } from 'react';
import fire from 'firebase';


class CookerComponent extends Component{
    constructor(props){
        super(props)
        this.state={
            requestList: []
        }
    this.getRequestFirebase = () => {
        return fire.firestore().collection('requests')
        .get()
        .then((request) => {
            const requestList = []
            request.forEach(doc => requestList.push(doc.data()))
            this.setState({requestList})
        })
        .catch(err => err)
        }

        this.createRestaurantManager = () => {
             return this.state.requestList.map((actualRequest, index) => (
                 actualRequest.products.map((arrRequest, index) => (
                     <div key={index}>
                     <p key={index}>Mesa {actualRequest.table}</p>
                    <p> {arrRequest.quantity} </p>
                     <p> {arrRequest.name} </p>
                     <p> {arrRequest.options || ''} </p>
                     <p> {arrRequest.extras || ''} </p>

                     </div>
                 ))



                // actualRequest.products.name,
                // actualRequest.products.quantity
             ))
        }
    }

    componentWillMount(){
        this.getRequestFirebase()
    }



    render(){
        return(
            <div>
                {this.createRestaurantManager()}
                <button>Preparar</button>
            </div>
        )
    }
}


export default CookerComponent
