import React, { Component } from "react";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import Typography from "@material-ui/core/Typography";
import { withStyles } from "@material-ui/core/styles";
import fire from 'firebase'
import moment from 'moment'

class ProfileTabs extends Component {
  constructor(props) {
    super(props)
    this.state = {
      menu: {},
      activeIndex: 0,
    };
  }

  handleChange = (_, activeIndex) => this.setState({ activeIndex });

  getMenu () {
    return fire.firestore().collection('menu')
    .get()
    .then((snap) => {
      snap.forEach(doc => {
        if(
          moment() >= moment(doc.data().initiate_at, 'HH:mm') 
          && moment() <= moment(doc.data().end_at, 'HH:mm') 
        ){
            this.setState({ menu: doc.data() })
        }
      }) 
    }) 
    .catch(err => console.log(err))
  }

  async componentDidMount(){
    await this.getMenu()
    console.log(this.state.menu)
  }

  handleLabel() {
    const menuLabel = Object.keys(this.state.menu);

    menuLabel.forEach((labelToRender) => {
      <VerticalTabs value={activeIndex} onChange={this.handleChange}  >

          <MyTab label = {labelToRender} />

        </VerticalTabs>
    })
  }

  render() {
    const { activeIndex } = this.state;
    return (
      <div
        style={{
          display: "flex"
        }}
      >



        {activeIndex === 0 && <TabContainer> Lanches </TabContainer>}
        {activeIndex === 1 && <TabContainer>Acompanhamentos</TabContainer>}
        {activeIndex === 2 && <TabContainer>Bebidas</TabContainer>}
        {activeIndex === 3 && <TabContainer>Resumo</TabContainer>}
      </div>
    );
  }
}

const VerticalTabs = withStyles(theme => ({
  flexContainer: {
    flexDirection: "column"
  },
  indicator: {
    display: "none"
  }
}))(Tabs);

const MyTab = withStyles(theme => ({
  root: {
    backgroundColor: "#e1e1e1",
    borderRight: '1px solid #737373',
    color: "white",

  },
  wrapper: {
    backgroundColor: "#737373",
    padding: theme.spacing(2),
  },
  selected: {
    borderRight: "4px solid #34b6a6",
  }
}))(Tab);

function TabContainer(props) {
  return (
    <Typography component="div" style={{ padding: 24 }}>
      {props.children}
    </Typography>
  );
}

export default ProfileTabs;
