import React, { Component } from 'react'
// import moment from 'moment'
import fire from '../config/firebase';
import moment from 'moment';



class MenuComponent extends Component {
    constructor(props){
        super(props) 
        this.state = {
            menu: null
        }
    }

    getMenu () {
        return fire.firestore().collection('menu')
        .get()
        .then((snap) => {
            snap.forEach(doc => {
                if (moment().isBefore('10:30', 'HH:mm')){
                    this.setState({ menu: doc.data() })
                    console.log(doc.data().name.breakfast) 
                } else {
                    console.log(doc.data().other_meals)  
                }


              })
        }) 
        .catch(err => console.log(err))
    }

    componentDidMount(){
        this.getMenu()
    }


    render() {
        return(
            <h1> menu </h1>
        )
    }
}

export default MenuComponent

