import React, { Component } from 'react';
import fire from 'firebase';
import Button from '@material-ui/core/Button';



class CookerComponent extends Component{
    constructor(props){
        super(props)
        this.state={
            requestList: []            
        }
    this.getRequestFirebase = () => {
        return fire.firestore().collection('requests')
        .get()
        .then((request) => {
            const requestList = []
            request.forEach(doc => {
                console.log(doc)
              requestList.push({...doc.data(), id: doc.uid}) })
            this.setState({requestList})
        })
        .catch(err => err)
        }

        this.createRestaurantManager = () => {
             return this.state.requestList.map((actualRequest,) => (
                 actualRequest.products.map((arrRequest, index) => (
                     <div key={index}>
                     <p>Mesa {actualRequest.table}</p>
                     <p> {arrRequest.quantity} </p>
                     <p> {arrRequest.name} </p>
                     <p> {arrRequest.options || ''} </p>
                     <p> {arrRequest.extras ? arrRequest.extras.name : ''} </p>
                 <Button onClick={() => this.updateRequest(actualRequest.id, actualRequest.status )} disabled={actualRequest.status === 'doing'}>{this.buttonName(actualRequest.status)}</Button>
                     </div>
                 ))
             ))
        }

        this.updateRequest = async (id , status) => {            
            console.log('id', id, 'status', status)

            await fire.firestore().collection('requests').doc(id).update({status});
            this.getRequestFirebase()
        }

        this.buttonName = (status) => {
            if(status === 'toDo') {
                return 'Preparar'
            }
            else if(status === 'doing'){
                return 'Em preparo...'
            }
            else if(status === 'done') {
                return 'Pronto !'
            }
        }
    }

    componentWillMount(){
        this.getRequestFirebase()
    }



    render(){
        return(
            <div>
                {this.createRestaurantManager()}

            </div>
        )
    }
}


export default CookerComponent
