import React, {
  Component,
  Fragment
} from 'react';
import {
  Button,
  TextField
} from '@material-ui/core';
import fire from '../../config/firebase';
import Logo from '../../assets/logo/logo.png';


class Login extends Component {
  constructor(props) {
    super(props)
    this.state = {
      email: '',
      password: ''
    }

    this.handleChange = this.handleChange.bind(this);
    this.login = this.login.bind(this)
  }

  login(e) {
    e.preventDefault();
    fire.auth().signInWithEmailAndPassword(this.state.email, this.state.password).then((u) => {
      console.log('funcionou')
    }).catch((error) => {
      console.log(error);
    });
  }

  handleChange(e) {
    this.setState({
      [e.target.name]: e.target.value
    });
  }

  render() {
    return ( <
      div className = '' >
      <
      Fragment >
      <
      img style = {
        {
          maxWidth: '200px'
        }
      }
      src = {
        Logo
      }
      alt = 'Logo' / >
      <
      /Fragment> <
      form onSubmit = {
        this.login
      } >
      <
      TextField value = {
        this.state.email
      }
      onChange = {
        this.handleChange
      }
      type = "email"
      placeholder = "email"
      variant = "filled"
      name = 'email' /
      >
      <
      TextField value = {
        this.state.password
      }
      onChange = {
        this.handleChange
      }
      type = "password"
      placeholder = "senha"
      variant = "filled"
      name = 'password' /
      >
      <
      Button type = 'submit' > Entrar < /Button> <
      /form> <
      /div>
    )
  }
}







export default Login
